import {Link} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCalendar, faCirclePlus,faMagnifyingGlass} from '@fortawesome/free-solid-svg-icons'
import {useEffect, useState} from "react";

function HomePage() {
    const [data1, setData] = useState([])
    const [show,setHide]=useState('false')
    const [selectedValues, setSelectedValues] = useState([]);
    const [checkedValues, setCheckedValues] = useState([]);
    const [uniqueCheckedValues, setUniqueCheckedValues] = useState([]);
    const [data2,setData2]=useState([])
    const [showMore, setShowMore] = useState(false);


    const handleCheckboxChange = (e) => {
        const value = e.target.value;
        const isChecked = e.target.checked;

        console.log(" e.target.value", e.target.value)
        console.log("isChecked",isChecked)


        setCheckedValues(prev => {
            let updatedValues;

            if (isChecked) {
                updatedValues = [...prev, value];

                console.log("updatedValues1",updatedValues)
            } else {
                updatedValues = prev.filter((item) => item !== value);

                console.log("updatedValues2",updatedValues)

            }

            const filteredData =
                data2.filter((item) => updatedValues.includes(item.location));
            setData(filteredData);

            return updatedValues;
        });
